// Usamos el modo estricto de javascript para evitar errores en el código javascript de la página web (use strict)
'use strict';

// Custom event for ready udc-framework
(function() {
  // Variable de la URL para obtener la URl segun ambiente.
  var ROOT = 'http://localhost:8888/';

  // Rutas de los archivos
  // Rura de los archivos assets
  var ASSETS_PATH = ROOT + 'assets/';
  // Ruta de los archivos de scripts
  var SCRIPTS_PATH = ASSETS_PATH + 'js/';
  // Ruta de las librerias
  var LIBS_PATH = ROOT + 'lib/';
  
  // Scripts a cargar en la página web (scriptsToLoad)
  var scriptsToLoad = [
    // 'vendor/pace.min.js',
    'main.min.js',
    'custom.min.js',
    'components.min.js'
  ];

  // Cargamos los scripts de la página web de forma asíncrona
  scriptsToLoad.forEach(function(script) {
    // Creamos el elemento script
    var scriptElement = document.createElement('script');
    // Indicamos el tipo de script
    scriptElement.type = 'text/javascript';
    // Indicamos la ruta del script
    scriptElement.src = SCRIPTS_PATH + script;
    // Agregamos el script al head de la página web
    document.head.appendChild(scriptElement);
  });
  

  // Cargamos el script de bootstrap de forma asíncrona
  // Creamos el elemento script
  var bs = document.createElement('script');
  // Indicamos el tipo de script
  bs.type = 'text/javascript';
  // Indicamos que el script se cargará de forma asíncrona
  bs.async = true;
  // Indicamos la ruta del script
  bs.src = LIBS_PATH + 'bootstrap/5.2.0/js/bootstrap.bundle.min.js';
  // Obtenemos el primer elemento script del head de la página web
  var firstScript = document.getElementsByTagName('script')[0];
  document.head.insertBefore(bs, firstScript);
})();