// Variable de la URL para obtener la URl segun ambiente.
var ROOT = 'http://localhost:8888/';

// Código para cargar los componentes de la página web (lo que contenga la variable IMPORT_COMPONENTS)
if (typeof IMPORT_COMPONENTS === 'undefined') {
    var IMPORT_COMPONENTS = [];
  }
if(IMPORT_COMPONENTS != null && IMPORT_COMPONENTS.length > 0) loadComponents();

// Función para cargar los componentes de la página web 
function loadComponents() {
    for (const component of IMPORT_COMPONENTS) {
        // Creamos el elemento script
        var scriptElement = document.createElement('script');
        // Indicamos el tipo de script
        scriptElement.type = 'text/javascript';
        // Indicamos la ruta del script
        scriptElement.src = `${ROOT}components/${component}.component.js`;
        // Agregamos el script al head de la página web
        document.head.appendChild(scriptElement);
    }
}

function loadTabla(selector, newHeader, newData) {
    if(window.customElements.get('v-table')) {
        // Obtén una referencia al componente v-table
        const table = document.querySelector(selector);
            
        // Actualiza los datos del componente v-table
        table.setAttribute('header', JSON.stringify(newHeader));
        table.setAttribute('data', JSON.stringify(newData));
    
        // Llama a connectedCallback() para que se actualice la tabla con los nuevos datos
        table.connectedCallback();
    }
}